import re

# Pega output da LLM
input_data = items[0].json

# Tenta diferentes campos poss√≠veis
output_llm = None
if 'output' in input_data:
    output_llm = input_data['output']
elif 'text' in input_data:
    output_llm = input_data['text']
elif 'content' in input_data:
    output_llm = input_data['content']
elif 'response' in input_data:
    output_llm = input_data['response']
else:
    for key, value in input_data.items():
        if isinstance(value, str) and len(value) > 100:
            output_llm = value
            break

if not output_llm:
    return [{'json': {'error': 'Output da LLM n√£o encontrado'}}]

# Parse dos testes
tests = []
blocks = output_llm.split('\n---\n')

for block in blocks:
    block = block.strip()

    if not block or not block.startswith('TESTE'):
        continue

    test = {}

    # ========================================
    # ADICIONA O TESTE COMPLETO/INTEIRO
    # ========================================
    test['teste_completo'] = block

    try:
        # Parse ID e Nome
        first_line = block.split('\n')[0]
        match = re.match(r'TESTE (\d+) - (.+)', first_line)
        if match:
            test['id'] = match.group(1)
            test['nome'] = match.group(2).strip()

        # Parse PERSONA
        persona_match = re.search(r'üë§ PERSONA:\s*\n(.+?)(?=\n\n|\nüé≠)', block, re.DOTALL)
        if persona_match:
            test['persona'] = persona_match.group(1).strip()

        # Parse COMPORTAMENTO
        comportamento_match = re.search(r'üé≠ COMPORTAMENTO:\s*\n(.+?)(?=\n\n|\nüí¨)', block, re.DOTALL)
        if comportamento_match:
            test['comportamento'] = comportamento_match.group(1).strip()

        # Parse MENSAGEM
        mensagem_match = re.search(r'üí¨ MENSAGEM INICIAL:\s*\n(.+?)(?=\n\n|\n‚úÖ)', block, re.DOTALL)
        if mensagem_match:
            test['mensagem'] = mensagem_match.group(1).strip()

        # Parse VALIDA√á√ïES
        validacoes_match = re.search(r'‚úÖ O QUE VALIDAR:\s*\n(.+?)$', block, re.DOTALL)
        if validacoes_match:
            validacoes_text = validacoes_match.group(1)
            validacoes = re.findall(r'\[ \] (.+)', validacoes_text)
            test['validacoes'] = validacoes
            test['total_validacoes'] = len(validacoes)
        else:
            test['validacoes'] = []
            test['total_validacoes'] = 0

        tests.append(test)

    except Exception as e:
        test['parse_error'] = str(e)
        tests.append(test)

# Retorna cada teste como um item separado
if tests:
    return [{'json': test} for test in tests]
else:
    return [{'json': {'error': 'Nenhum teste encontrado'}}]
